<template> <div class="auth-container"> <div class="auth-card"> <div class="auth-header"> <i class="bi bi-kanban-fill text-primary display-4"></i> <h2 class="mt-3">Create Account</h2> <p class="text-muted">Sign up to get started</p> </div> <form @submit.prevent="handleRegister" class="auth-form"> <div class="mb-3"> <label class="form-label">Full Name</label> <input v-model="formData.name" type="text" class="form-control" :class="{ \"is-invalid\": errors.name }" placeholder="Enter your name" required /> <div v-if="errors.name" class="invalid-feedback">{{ errors.name }}</div> </div> <div class="mb-3"> <label class="form-label">Email</label> <input v-model="formData.email" type="email" class="form-control" :class="{ \"is-invalid\": errors.email }" placeholder="Enter your email" required /> <div v-if="errors.email" class="invalid-feedback">{{ errors.email }}</div> </div> <div class="mb-3"> <label class="form-label">Password</label> <input v-model="formData.password" type="password" class="form-control" :class="{ \"is-invalid\": errors.password }" placeholder="Create a password" required /> <div v-if="errors.password" class="invalid-feedback">{{ errors.password }}</div> </div> <div class="mb-3"> <label class="form-label">Confirm Password</label> <input v-model="formData.password_confirmation" type="password" class="form-control" :class="{ \"is-invalid\": errors.password_confirmation }" placeholder="Confirm your password" required /> <div v-if="errors.password_confirmation" class="invalid-feedback">{{ errors.password_confirmation }}</div> </div> <div v-if="errorMessage" class="alert alert-danger">{{ errorMessage }}</div> <button type="submit" class="btn btn-primary w-100" :disabled="loading"> <span v-if="loading" class="spinner-border spinner-border-sm me-2"></span> Create Account </button> </form> <div class="auth-footer"> <p class="mb-0">Already have an account? <router-link to="/login">Sign in</router-link></p> </div> </div> </div> </template> <script setup lang="ts"> import { ref } from \"vue\" import { useRouter } from \"vue-router\" import { useAuthStore } from \"@/stores/auth\" const router = useRouter() const authStore = useAuthStore() const formData = ref({ name: \"\", email: \"\", password: \"\", password_confirmation: \"\" }) const errors = ref<Record<string, string>>({}) const errorMessage = ref(\"\") const loading = ref(false) const handleRegister = async () => { try { loading.value = true errors.value = {} errorMessage.value = \"\" await authStore.register(formData.value) router.push(\"/dashboard\") } catch (error: any) { if (error.response?.data?.errors) { errors.value = error.response.data.errors } else { errorMessage.value = error.response?.data?.message || \"Registration failed. Please try again.\" } } finally { loading.value = false } } </script> <style scoped> .auth-container { min-height: 100vh; display: flex; align-items: center; justify-content: center; background: linear-gradient(135deg, #667eea 0%, #764ba2 100%); padding: 20px; } .auth-card { background: white; border-radius: 12px; box-shadow: 0 10px 40px rgba(0, 0, 0, 0.1); padding: 40px; width: 100%; max-width: 420px; } .auth-header { text-align: center; margin-bottom: 32px; } .auth-header h2 { font-weight: 700; color: var(--text-dark); } .auth-form .form-label { font-weight: 500; color: var(--text-dark); margin-bottom: 8px; } .auth-form .form-control { padding: 12px 16px; border: 1px solid var(--border-color); border-radius: 8px; } .auth-form .form-control:focus { border-color: var(--primary-color); box-shadow: 0 0 0 3px rgba(37, 99, 235, 0.1); } .auth-form .btn-primary { padding: 12px; font-weight: 600; border-radius: 8px; margin-top: 16px; } .auth-footer { text-align: center; margin-top: 24px; padding-top: 24px; border-top: 1px solid var(--border-color); } .auth-footer a { color: var(--primary-color); font-weight: 600; text-decoration: none; } .auth-footer a:hover { text-decoration: underline; } </style>
